{!REQUIRESCRIPT("/soap/ajax/36.0/apex.js")} 
var caseObj = new sforce.SObject("Case");
caseObj.Acepto_visita__c = "{!Case.Acepto_visita__c}";
caseObj.Profesional__c = "{!Case.Profesional_Best_Care__c}";
caseObj.Id = "{!Case.Id}";

if(caseObj.Acepto_visita__c != 'No'){
    caseObj.Acepto_visita__c = 'No';
    caseObj.Profesional_Best_Care__c = null;
    alert('Visita rechazada');
} else{
    alert('Ya rechaz√≥ la visita');
}
var result = sforce.connection.update([caseObj]);
if(result[0].getBoolean("success"))
{
   window.location.reload();
}
else{
  alert('Error : '+result);
}
