WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/42.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/42.0/apex.js")}
{!REQUIRESCRIPT("/support/console/42.0/integration.js")}

var retStr = sforce.apex.execute(
    "XappiaHelper", 
    "getRecordTypeToTratamiento",
    {'SobjectType': 'Tratamiento__c', 'recordTypeName': 'Tratamiento Best Care'}
);

var url = '/a07/e?' + 'CF00N1I00000MeAVl={! JSENCODE(Account.Name) }&CF00N1I00000MeAVl_lkid={! JSENCODE(Account.Id) }&RecordType=' + retStr; 

if (sforce.console.isInConsole()) {
    var openSubtab = function(result){ 
        sforce.console.openSubtab(
            result.id,
            url + '&inConsole=true',
            true, 
            'Nuevo Tratamiento',
            null
);}; 
    sforce.console.getEnclosingPrimaryTabId(openSubtab);
} else {
    window.parent.location.href = url;
}
